import { Component } from '@angular/core';

@Component({
  selector: 'app-contador',
  templateUrl: './contador.component.html',
  styleUrls: ['./contador.component.css']
})
export class ContadorComponent {
  public contador:number;
  constructor() {
    this.contador=20;
  }

  increment(){
    this.contador+=1
  }
  decrement(){
    this.contador-=1
  }
  resetValue(){
    this.contador=20
  }
}
<div class="container-fluid" id="contador_container">
    <div class="contador_title">
        <h1>Contador</h1>
    </div>
    <div class="contador_count">
        <h3>{{contador}}</h3>
    </div>
    <div class="contador_increment">
        <button class="btn btn-danger" ng-click="increment()">+1</button>
    </div>
    <div class="contador_decrement">
        <button class="btn btn-danger" ng-click="decrement()">-1</button>
    </div>
    <div class="contador_reset">
        <button class="btn btn-danger" ng-click="resetValue()">Reset</button>
    </div>
</div>
