import { Component } from '@angular/core';

@Component({
  selector: 'app-contador',
  templateUrl: './contador.component.html',
  styleUrls: ['./contador.component.css']
})
export class ContadorComponent {
  public contador:number;
  public base:number;
  constructor() {
    this.contador=20;
    this.base=5;
  }

  increment(base:number){
    this.contador+=this.base
  }
  decrement(base:number){
    this.contador-=this.base
  }
  resetValue(){
    this.contador=20
  }
}
<div class="container-fluid" id="contador_container">
    <div class="contador_title">
        <h1>Contador</h1>
    </div>
    <div class="contador_count">
        <h3>{{contador}}</h3>
    </div>
    <div class="contador_increment">
        <button class="btn btn-danger" (click)="increment(base)">+{{base}}</button>
    </div>
    <div class="contador_decrement">
        <button class="btn btn-danger" (click)="decrement(base)">-{{base}}</button>
    </div>
    <div class="contador_reset">
        <button class="btn btn-danger" (click)="resetValue()">Reset</button>
    </div>
</div>
